const getMetricIcon = (metricType: string) => {
  switch (metricType) {
    case 'cpu_temperature':
      return Thermometer;
    case 'gpu_temperature':
      return Thermometer;
    case 'cpu_usage':
      return Cpu;
    case 'memory_usage':
      return Activity;
    case 'disk_usage':
      return HardDrive;
    default:
      return Activity;
  }
};

const getMetricStatus = (metricType: string, value: number) => {
  if (metricType.includes('temperature')) {
    if (value >= 90) return 'critical';
    if (value >= 80) return 'warning';
    if (value <= 60) return 'good';
    return 'normal';
  }
  if (metricType.includes('usage')) {
    if (value >= 95) return 'critical';
    if (value >= 85) return 'warning';
    if (value <= 50) return 'good';
    return 'normal';
  }
  return 'normal';
};

const getStatusColor = (metricType: string, value: number) => {
  const status = getMetricStatus(metricType, value);
  switch (status) {
    case 'critical':
      return 'bg-red-500';
    case 'warning':
      return 'bg-yellow-500';
    case 'good':
      return 'bg-green-500';
    default:
      return 'bg-gray-500';
  }
};

export default Dashboard;
